function GM:GetGameDescription()	return self.schema:GetName() or "Gamemode Framework"endfunction GM:GetFallDamage(client, speed)	return (speed - 580) * 0.8endlocal function RemoveEntitiesByClass(class)	for _, v in pairs(ents.FindByClass(class)) do		SafeRemoveEntity(v)	endendfunction GM:InitPostEntity()	RemoveEntitiesByClass("item_healthcharger")	RemoveEntitiesByClass("prop_vehicle*")	RemoveEntitiesByClass("weapon_*")	RemoveEntitiesByClass("item_suitcharger")endfunction GM:ShowHelp(client)end