DeriveGamemode("base")gframework = GMgfw = gframeworkfunction gframework:Include(path)	local prefix = path:match("(%a-)_%w+.lua$")	if prefix == "cl" then		if CLIENT then			include(path)		else			AddCSLuaFile(path)		end	elseif prefix == "sh" then		if SERVER then			AddCSLuaFile(path)		end		include(path)	elseif prefix == "sv" then		if SERVER then			include(path)		end	else		include(path)	endendfunction gframework:IncludeDirectory(path, frombase)	if (not frombase or path:match("^schema/")) and not gframework.schema then		error("too early to include schema directories")	end	path = (frombase and "gframework" or gframework.schema.FolderName) .. "/gamemode/" .. path .. "/"	local searchpath = path .. "*.lua"	for _, v in pairs(file.Find(searchpath, "LUA")) do		self:Include(path .. v)	endendfunction gframework:IncludeLibrary(name, isdir)	if isdir then		local sv_file = ("gframework/gamemode/libraries/%s/server.lua"):format(name)		local sh_file = ("gframework/gamemode/libraries/%s/shared.lua"):format(name)		local cl_file = ("gframework/gamemode/libraries/%s/client.lua"):format(name)		if file.Exists(sv_file, "LUA") and SERVER then			include(sv_file)		elseif file.Exists(cl_file, "LUA") and CLIENT then			include(cl_file)		elseif file.Exists(sh_file, "LUA") then			include(sh_file)		end	else		local sh_file = ("gframework/gamemode/libraries/sh_%s.lua"):format(name)		local sv_file = ("gframework/gamemode/libraries/sv_%s.lua"):format(name)		local cl_file = ("gframework/gamemode/libraries/cl_%s.lua"):format(name)		if file.Exists(sh_file, "LUA") then			if SERVER then				AddCSLuaFile(sh_file)			end			include(sh_file)		end		if file.Exists(sv_file, "LUA") and SERVER then			include(sv_file)		end		if file.Exists(cl_file, "LUA") then			if CLIENT then				include(cl_file)			else				AddCSLuaFile(cl_file)			end		end	endendfunction gframework:IncludeLibraries()	local included_libs = {}	local path = "gframework/gamemode/libraries/"	local files, directories = file.Find(path .. "*", "LUA")	for _, v in pairs(files) do		local match = v:match("^%a%a_(%w+)%.lua$")		if not included_libs[match] then			included_libs[match] = true			self:IncludeLibrary(match, false)		end	end	for _, v in pairs(directories) do		if not included_libs[v] then			included_libs[v] = true			self:IncludeLibrary(v, true)		end	endendgframework:IncludeLibraries()gframework:IncludeDirectory("core", true)gframework:IncludeDirectory("gui", true)